frappe.ui.form.on('Item', {
    refresh: function(frm) {
        if (!frm.doc.__islocal && !frm.doc.is_template && frm.doc.is_stock_item === 0) {

            frm.add_custom_button("Create Product Bundle", function () {

                frappe.call({
                    method: "frappe.client.get_list",
                    args: {
                        doctype: "Product Bundle",
                        filters: {
                            new_item_code: frm.doc.name
                        },
                        limit_page_length: 1
                    },
                    callback: function(r) {
                        if (r.message && r.message.length > 0) {
                            frappe.msgprint({
                                title: "Bundle Already Exists",
                                message: "A product bundle already exists for this item.",
                                indicator: "orange"
                            });
                        } else {
                            // Create the new Product Bundle doc and route to it
                            frappe.model.with_doctype("Product Bundle", function () {
                                let doc = frappe.model.get_new_doc("Product Bundle");
                                doc.new_item_code = frm.doc.name;
                                frappe.set_route("Form", "Product Bundle", doc.name);
                            });
                        }
                    }
                });

            }, __("Actions"));
        }
    }
});

frappe.ui.form.on('Purchase Receipt', {
    after_save(frm) {
        let total_qty = 0;

        (frm.doc.custom_packed_items || []).forEach(row => {
            total_qty += flt(row.qty);
        });

        frm.set_value("custom_total_bundle_item_quantity", total_qty);

        // Save again to persist the value (if not submitted)
        if (frm.doc.docstatus === 0) {
            frm.save();
        }
    }
});

frappe.ui.form.on('Warranty', {
    validate: function(frm) {
        let total_received = 0;
        let total_claimed = 0;

        (frm.doc.warranty_item_detail || []).forEach(row => {
            // Calculate and update balance for each row
            row.balance_quantity = flt(row.quantity_received) - flt(row.quantity_claimed);
            total_received += flt(row.quantity_received);
            total_claimed += flt(row.quantity_claimed);
        });

        frm.set_value('total_items_received', total_received);
        frm.set_value('total_items_claimed', total_claimed);
        frm.set_value('total_items_balanced', total_received - total_claimed);

        frm.refresh_field("warranty_item_detail");
    }
});

frappe.ui.form.on('Delivery Note', {
  refresh: function (frm) {
    if (!frm.custom_buttons_added) {
      frm.add_custom_button('üîç Check Stock', () => open_stock_dialog());
      frm.custom_buttons_added = true;
    }
  }
});

function open_stock_dialog() {
  const d = new frappe.ui.Dialog({
    title: "Stock Check",
    size: "large",
    fields: [
      { fieldtype: "Section Break", label: "Filters" },
      { label: "Item", fieldname: "item_code", fieldtype: "Link", options: "Item" },
      { fieldtype: "Column Break" },
      { label: "Item Group", fieldname: "item_group", fieldtype: "Link", options: "Item Group" },
      { fieldtype: "Column Break" },
      { label: "Warehouse", fieldname: "warehouse", fieldtype: "Link", options: "Warehouse" },
      { fieldtype: "Section Break" },
      { label: "Exclude Zero Quantity", fieldname: "exclude_zero_qty", fieldtype: "Check", default: 1 },
      { fieldtype: "Section Break" },
      { label: "Check Stock", fieldname: "check_stock", fieldtype: "Button" },
      { fieldtype: "HTML", fieldname: "min_qty_html" },
      { fieldtype: "HTML", fieldname: "results_html" }
    ],
    primary_action_label: "Close",
    primary_action: () => d.hide()
  });

  d.fields_dict.check_stock.input.onclick = async () => {
    const values = d.get_values();
    if (!values) return;

    const res = await frappe.call({
      method: "hanif_traders.api.stock_check.get_stock_balance",
      args: {
        item_code: values.item_code,
        item_group: values.item_group,
        warehouse: values.warehouse
      }
    });

    const data = res.message || [];
    const excludeZero = values.exclude_zero_qty;

    const formattedData = data
      .filter(row => !excludeZero || row.actual_qty > 0)
      .map(row => ({
        'Item': row.item_code || "",
        'Item Name': row.item_name || "",
        'Item Group': row.item_group || "",
        'Warehouse': row.warehouse || "",
        'Actual Qty': row.actual_qty || 0,
        'Required Qty': row.required_qty || null,
        'Is Non-Stock': row.is_stock_item ? "‚ùå" : "‚úÖ"
      }));

    // Calculate min qty for bundle items
    let min_qty = null;
    const bundleRows = data.filter(row => row.required_qty);
    if (bundleRows.length > 0) {
      min_qty = Math.min(...bundleRows.map(row =>
        row.required_qty > 0 ? Math.floor(row.actual_qty / row.required_qty) : Infinity
      ));
    }

    const min_html = d.fields_dict.min_qty_html.$wrapper;
    min_html.empty();
    if (min_qty !== null && isFinite(min_qty)) {
      min_html.html(`<div style="margin-bottom:10px;font-weight:bold;color:#0a58ca;">
        ‚û§ Minimum Available Quantity (based on bundle children): ${min_qty}
      </div>`);
    }

    const wrapper = d.fields_dict.results_html.$wrapper;
    wrapper.empty();

    const table_container = document.createElement('div');
    wrapper.append(table_container);

    new frappe.DataTable(table_container, {
      columns: [
        { name: 'Item', width: 200, format: (value) => `<a href="/app/item/${value}" target="_blank">${value}</a>` },
        { name: 'Item Name', width: 300 },
        { name: 'Item Group', width: 200 },
        { name: 'Warehouse', width: 200 },
        { name: 'Actual Qty', align: 'right', width: 120 },
        { name: 'Required Qty', align: 'right', width: 120 },
        { name: 'Is Non-Stock', width: 120 }
      ],
      data: formattedData,
      layout: 'fixed',
      noDataMessage: "No items matched your filters.",
      inlineFilters: true,
      enableSorting: true
    });
  };

  d.show();
}

frappe.ui.form.on('Complain', {
  refresh(frm) {
    if (frm.doc.workflow_state === 'Assigned' && frm.doc.complain_csc) {
      frm.add_custom_button('üîê Verify CSC', () => {
        const d = new frappe.ui.Dialog({
          title: 'Verify CSC Code',
          fields: [{
            fieldtype: 'Data',
            fieldname: 'csc_input',
            label: '4-digit Code',
            reqd: 1
          }],
          primary_action(values) {
            d.hide();
            frappe.call({
              method: 'hanif_traders.api.complain.verify_csc',
              args: {
                complain_name: frm.doc.name,
                input_code: values.csc_input
              },
              callback: r => {
                if (r.exc) {
                  frappe.msgprint(`‚ùå ${r.exc}`);
                } else {
                  frappe.msgprint(`‚úÖ ${r.message}`);
                  frm.reload_doc();
                }
              }
            });
          }
        });
        d.show();
      });
    }
  }
});

frappe.ui.form.on('Complain', {
  refresh: function (frm) {
    if (frm.doc.workflow_state === "Assigned" && frm.doc.complain_csc) {

      frm.add_custom_button('üîê Verify CSC', () => {
        frappe.prompt([
          {
            label: 'Enter CSC Code',
            fieldname: 'entered_csc',
            fieldtype: 'Data',
            reqd: true
          }
        ],
        async (values) => {
          if (values.entered_csc === frm.doc.complain_csc) {
            // ‚úÖ CSC matched
            frappe.msgprint("‚úÖ CSC verified successfully!");

            await frm.set_value('workflow_state', 'CSC Verified');
            await frm.save(); // or frm.submit() if needed

          } else {
            frappe.msgprint({
              title: "‚ùå Verification Failed",
              message: "The entered CSC code is incorrect.",
              indicator: "red"
            });
          }
        },
        'Verify CSC Code');
      });
    }
  }
});
