script:
stock_entries = frappe.get_all("Stock Entry", filters={"custom_purchase_receipt": doc.name})

for entry in stock_entries:
    se = frappe.get_doc("Stock Entry", entry.name)
    if se.docstatus == 1:
        se.cancel()

def update_warranty_balance(doc, reverse=False):
    for row in doc.warranty_item_detail:
        if not row.item_code:
            continue

        key = {"customer": doc.customer, "item_code": row.item_code}
        existing = frappe.db.get_value("Warranty Balance", key, ["name", "quantity_received", "quantity_claimed"])

        delta_received = row.quantity_received or 0
        delta_claimed = row.quantity_claimed or 0

        if reverse:
            delta_received *= -1
            delta_claimed *= -1

        if existing:
            name, received, claimed = existing
            updated_received = (received or 0) + delta_received
            updated_claimed = (claimed or 0) + delta_claimed
            frappe.db.set_value("Warranty Balance", name, {
                "quantity_received": updated_received,
                "quantity_claimed": updated_claimed,
                "balance_quantity": updated_received - updated_claimed
            })
        else:
            frappe.get_doc({
                "doctype": "Warranty Balance",
                "customer": doc.customer,
                "item_code": row.item_code,
                "quantity_received": delta_received,
                "quantity_claimed": delta_claimed,
                "balance_quantity": delta_received - delta_claimed
            }).insert()

# Call this at the bottom of your Warranty on_submit script
update_warranty_balance(doc)

total = 0
for row in doc.payment_details:
    print(f"Amount:",row.amount)
    if row.amount:
        total=total+row.amount    
doc.total_amount = total

if not doc.collected_by:
    employee= frappe.db.get_value("Employee",{"user_id":frappe.session.user},"employee_name")
    print(f"employee:",frappe.session.user)
    if employee:
        doc.collected_by = employee

if doc.receipt_name == "Manual":
    if not doc.receipt_number:
        frappe.throw("Please enter Receipt Number manually.")
    doc.name = doc.receipt_number
