script:
total = 0
for row in doc.payment_details:
    print(f"Amount:",row.amount)
    if row.amount:
        total=total+row.amount    
doc.total_amount = total

if not doc.collected_by:
    employee= frappe.db.get_value("Employee",{"user_id":frappe.session.user},"employee_name")
    print(f"employee:",frappe.session.user)
    if employee:
        doc.collected_by = employee

if doc.receipt_name == "Manual":
    if not doc.receipt_number:
        frappe.throw("Please enter Receipt Number manually.")
    doc.name = doc.receipt_number
